import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';

const pages = ['Music', 'Podcast', 'Live'];
const settings = ['Profile', 'Account', 'Dashboard', 'Logout'];

// Update searchBarStyles for reduced height and centering
const searchBarStyles = {
  position: 'relative',
  borderRadius: '999px',
  backgroundColor: 'rgba(255,255,255,0.15)',
  boxShadow: '0 2px 8px 0 rgba(60,60,60,0.10)',
  transition: 'background-color 0.3s, box-shadow 0.3s',
  '&:hover': {
    backgroundColor: 'rgba(255,255,255,0.22)',
    boxShadow: '0 4px 16px 0 rgba(60,60,60,0.15)',
  },
  marginLeft: 2,
  width: { xs: '100%', md: '480px' },
  height: 36, // Reduced height
  display: 'flex',
  alignItems: 'center',
  px: 2,
};

const searchIconStyles = {
  color: '#fff',
  mr: 1,
  fontSize: 26,
  opacity: 0.85,
};

const inputStyles = {
  color: 'inherit',
  width: '100%',
  fontWeight: 500,
  letterSpacing: 0.5,
  '& .MuiInputBase-input': {
    p: 0,
    height: 28, // Thinner input
    fontSize: 16,
    transition: 'width 0.3s',
    '&:focus': {
      width: '100%',
    },
  },
};

function ResponsiveAppBar({ sx }) {
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElUser, setAnchorElUser] = React.useState(null);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  return (
    <AppBar
      position="fixed"
      sx={{
        bgcolor: '#424242',
        color: '#fff',
        ...sx
      }}
      elevation={1}
    >
      <Container maxWidth="xl">
        <Toolbar disableGutters sx={{ display: 'flex', justifyContent: 'space-between' }}>
          {/* LEFT: Nav buttons */}
          <Box
            sx={{
              display: { xs: 'none', md: 'flex' },
              alignItems: 'center',
              minWidth: 220,
            }}
          >
            {pages.map((page) => (
              <Button
                key={page}
                onClick={handleCloseNavMenu}
                sx={{
                  my: 2,
                  color: 'white',
                  display: 'block',
                  '&:hover': {
                    bgcolor: 'rgba(255, 255, 255, 0.1)',
                  },
                }}
              >
                {page}
              </Button>
            ))}
          </Box>

          {/* CENTER: Search bar */}
          <Box
            sx={{
              flexGrow: 1,
              display: { xs: 'none', md: 'flex' },
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <Box sx={searchBarStyles}>
              <SearchIcon sx={searchIconStyles} />
              <InputBase
                placeholder="Searchâ€¦"
                inputProps={{ 'aria-label': 'search' }}
                sx={inputStyles}
              />
            </Box>
          </Box>

          {/* RIGHT: Avatar/settings */}
          <Box sx={{ flexGrow: 0, display: 'flex', alignItems: 'center' }}>
            <Tooltip title="Open settings">
              <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                <Avatar
                  alt="User Avatar"
                  src="data:image/jpeg;base64," // <-- Add your image URL here
                />
              </IconButton>
            </Tooltip>
            <Menu
              sx={{ mt: '45px' }}
              id="menu-appbar"
              anchorEl={anchorElUser}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={Boolean(anchorElUser)}
              onClose={handleCloseUserMenu}
            >
              {settings.map((setting) => (
                <MenuItem key={setting} onClick={handleCloseUserMenu}>
                  <Typography textAlign="center">{setting}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box>

          {/* MOBILE: Hamburger menu and search bar */}
          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="open navigation"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorElNav}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
              open={Boolean(anchorElNav)}
              onClose={handleCloseNavMenu}
              sx={{ display: { xs: 'block', md: 'none' } }}
            >
              {pages.map((page) => (
                <MenuItem key={page} onClick={handleCloseNavMenu}>
                  <Typography sx={{ textAlign: 'center' }}>{page}</Typography>
                </MenuItem>
              ))}
              {/* Search bar in mobile menu */}
              <MenuItem disableRipple disableTouchRipple>
                <Box sx={{ width: '100%' }}>
                  <Box sx={searchBarStyles}>
                    <SearchIcon sx={searchIconStyles} />
                    <InputBase
                      placeholder="Searchâ€¦"
                      inputProps={{ 'aria-label': 'search' }}
                      sx={inputStyles}
                    />
                  </Box>
                </Box>
              </MenuItem>
            </Menu>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default ResponsiveAppBar;